import AcademicList from './AcademicList.vue';
export default {
  name: 'Semester',
  components: {
    AcademicList
  },
  props: ['academicRecords', 'open', 'index'],

  data() {
    return {
      collapsed: false,
      totalUnits: 0,
      totalGradePoints: 0,
      list: {}
    };
  },

  methods: {
    swap() {
      this.collapsed = !this.collapsed;
      this.open(this.index);
    }

  },

  mounted() {
    this.academicRecords.enrollments.forEach(e => {
      this.totalUnits += e.units;
      this.totalGradePoints += e.gradePoints;
    });
    this.list = {
      element: [['Units Earned', this.totalUnits], ['Term GPA', this.academicRecords.termGPA.toFixed(3)], ['Cumulative GPA', (this.academicRecords.totalGradePoints / this.academicRecords.totalUnits).toFixed(3)], ['Academic Standing', 'Good Standing']]
    };
  }

};
