  methods: {
    // Transfer the days to numbers
    daysToIndex(day) {
      switch (day) {
        case 'Mo':
          return 1;

        case 'Tu':
          return 2;

        case 'We':
          return 3;

        case 'Th':
          return 4;

        case 'Fr':
          return 5;

        case 'Sa':
          return 6;

        case 'Su':
          return 7;

        default:
          return -1;
      }
    },

    hello(i) {
      return this.repeat === 0;
    },

    // Initalise the Times array
    initTimes() {
      this.timetable = [[]];
      let element = {
        courseCode: '',
        color: '',
        repeat: 1
      };

      for (let i = 0; i < 10; i++) {
        this.timetable[i] = [];
        this.numOfCol[i] = 5;

        for (let j = 0; j < 5; j++) {
          this.timetable[i][j] = element;
        }
      }

      this.parseTimes();
    },

    // Add the events into Times array
    parseTimes() {
      this.courses.forEach(c => {
        let {
          courseCode,
          times,
          color
        } = c;
        times.forEach(t => {
          let {
            day,
            start,
            end
          } = t;
          start = Math.floor(parseInt(start) / 100);
          end = Math.floor(parseInt(end) / 100);
          day = this.daysToIndex(day);
          let repeat = end - start;
          let element = {
            courseCode,
            color,
            repeat
          };
          this.timetable[start - 8].splice(day - 1, 1, element);

          for (let i = 1; i <= repeat - 1; i++) {
            // element = {courseCode: 'hi', color: '', repeat: 0}
            // this.timetable[start - 8 + i].splice(day - 1, 1, element)
            this.numOfCol[start - 8 + 1] -= repeat - 1;
          }
        });
      });
      console.log(this.numOfCol);
      this.parsed = true;
    },

    // Get the elements from Times array
    getElement(e, i, j) {
      if (!this.parsed) {
        return '';
      }

      switch (e) {
        case 'courseCode':
          return this.timetable[i][j].courseCode;

        case 'color':
          return this.timetable[i][j].color;

        case 'repeat':
          return this.timetable[i][j].repeat;

        default:
          return null;
      }
    }

  },

  mounted() {
    this.$nextTick(this.initTimes());
  }

};
