import HomeImage from '../components/home/HomeImage.vue';
import AboutMe from '../components/home/AboutMe.vue';
import Timetable from '../components/home/Timetable.vue';
export default {
  name: 'Home',
  components: {
    HomeImage,
    AboutMe,
    Timetable
  },

  data() {
    return {
      sections: []
    };
  },

  methods: {
    onScroll(e) {
      this.sections.forEach(item => {
        // console.log(item.id);
        let s = document.getElementById(item.id);
        let judge = e.target.scrollTop >= s.offsetTop;

        if (judge) {}
      });
      let scrollItems = document.querySelectorAll(".scroll-item");

      for (let i = scrollItems.length - 1; i >= 0; i--) {
        // 判断滚动条滚动距离是否大于当前滚动项可滚动距离
        let judge = e.target.scrollTop >= scrollItems[i].offsetTop - scrollItems[0].offsetTop;

        if (judge) {
          this.activeStep = i;
          break;
        }
      }
    }

  },

  mounted() {
    this.pid = PubSub.subscribe('pages', (_, pages) => {
      this.sections = pages[0].sections;
    });
    window.addEventListener('scroll', this.onScroll);
  }

};
