import Nav from '../Nav.vue';
export default {
  name: 'HomeImage',
  components: {
    Nav
  },

  data() {
    return {
      curIndex: 0,
      images: [require('../../assets/img/A01.webp'), require('../../assets/img/A02.webp'), require('../../assets/img/A03.webp'), require('../../assets/img/A04.webp')]
    };
  },

  methods: {
    autoPlay() {
      this.curIndex = this.curIndex < this.images.length - 1 ? this.curIndex + 1 : 0;
    },

    play() {
      setInterval(this.autoPlay, 4000);
    }

  },

  mounted() {
    this.play();
  }

};
