// Import Components
import Introduction from '../components/homeSection/Introduction.vue';
import AboutMe from '../components/homeSection/About.vue';
import Education from '../components/homeSection/Education.vue';
import Skills from '../components/homeSection/Skills.vue';
import Timetable from '../components/homeSection/Timetable.vue'; // Import PubSub

import PubSub from 'pubsub-js';
export default {
  name: 'Home',
  components: {
    Introduction,
    AboutMe,
    Education,
    Skills,
    Timetable
  },
  methods: {
    // Detect current section when scrolling
    onScroll() {
      // For each section
      this.sections.forEach((item, index) => {
        let s = document.getElementById(item.id); // Check whether current position below the section

        if (window.scrollY >= s.offsetTop - 50) {
          // Set the index of active section through PubSub
          PubSub.publish('activeSection', index);
        }
      });
    }

  },

  mounted() {
    // Get the home sections through PubSub
    this.pid = PubSub.subscribe('pages', (_, pages) => {
      this.sections = pages[0].sections;
    }); // Listen scroll event

    window.addEventListener('scroll', this.onScroll);
  },

  beforeDestroy() {
    PubSub.unsubscribe(this.pid); // Unsubscribe pid
  }

};
