import Introduction from '../components/home/Introduction.vue';
import AboutMe from '../components/home/AboutMe.vue';
import Timetable from '../components/home/Timetable.vue';
import PubSub from 'pubsub-js';
export default {
  name: 'Home',
  components: {
    Introduction,
    AboutMe,
    Timetable
  },

  data() {
    return {
      sections: []
    };
  },

  methods: {
    onScroll() {
      this.sections.forEach((item, index) => {
        let s = document.getElementById(item.id);
        let judge = window.scrollY >= s.offsetTop - 50;

        if (judge) {
          PubSub.publish('activeSection', index);
        }
      });
    }

  },

  mounted() {
    this.pid = PubSub.subscribe('pages', (_, pages) => {
      this.sections = pages[0].sections;
    });
    window.addEventListener('scroll', this.onScroll);
  }

};
