export default {
  name: 'MainNav',
  props: ['pages'],

  data() {
    return {
      navStyle: 'home-top'
    };
  },

  methods: {
    onScroll() {
      let path = this.$router.currentRoute.path.split('/')[1];

      if (path === 'home' && window.top.scrollY < window.innerHeight / 2 && window.top.scrollY > 50) {
        console.log(path);
        this.changeNavStyle('nondisplay-nav');
        return;
      }

      if (window.top.scrollY < window.innerHeight / 2) {
        this.changeNavStyle(path + '-nav');
      } else {
        this.changeNavStyle(path + '-scroll');
      }
    },

    currentNav(path) {
      switch (path) {
        case '/home':
          this.changeNavStyle('home-nav');
          break;

        case '/about':
          this.changeNavStyle('about-nav');
          break;

        default:
          break;
      }
    },

    changeNavStyle(path) {
      this.navStyle = path;
    }

  },
  watch: {
    $route(to, from) {
      this.currentNav(to.path);
    }

  },

  mounted() {
    this.currentNav(this.$router.currentRoute.path);
    window.addEventListener('scroll', this.onScroll);
  }

};
